<style lang="scss" scoped>
@import "@/assets/scss/variables.scss";
@import "@/assets/scss/breakpoints.scss";
@import "@/assets/scss/global.scss";
@import "@/assets/scss/mixins.scss";

.hero {
    position: relative;
    &__bcg {
        width: 100%;
        height: 100%;
        object-fit: cover;
        position: absolute;
        left: 0;
        top: 0;
        z-index: 1;
    }
    &__wrapper {
        display: flex;
        align-items: center;
        justify-content: center;
        flex-direction: column;
        position: relative;
        z-index: 2;
        min-height: 900px;
    }
    &__logo {
        width: 100%;
    }
    &__button {
        margin-top: 88px;
        display: flex;
        align-items: center;
        background-color: $white;
        border-radius: 48px;
        padding: 10px 10px;
        text-decoration: none;
        transition: .3s ease;
        &:hover {
            .hero__calendar {
                opacity: 0;
                @include breakpoint-max('mobile') {
                    opacity: 1;
                }
            }
            span {
                opacity: 0;
                @include breakpoint-max('mobile') {
                    opacity: 1;
                }
            }
            .hero__arrow-box {
                transform: translateX(-245%);
                @include breakpoint-max('mobile') {
                    transform: rotate(-180deg);
                }
            }
        }
        span {
            margin: 0 56px;
            display: inline-block;
            font-size: 2.4rem;
            color: $black;
            font-weight: 400;
            transition: .3s ease;
            text-align: center;
            @include breakpoint-max('mobile') {
                margin: 0 40px;
            }
        }
    }
    &__calendar {
        transition: .3s ease;

    }
    &__arrow-box {
        background-color: $gold;
        padding: 20px;
        border-radius: 50%;
        transition: .3s .1s cubic-bezier(.17,.67,.19,.98);
        display: flex;
        align-items: center;
        justify-content: center;
    }
}
</style>